import { useState } from 'react';
import './App.scss';
import headerDesktop from '../images/bg-header-desktop.svg'
import headerMobile from '../images/bg-header-mobile.svg'
import photosnap from '../images/photosnap.svg'
import manage from '../images/manage.svg'
import account from '../images/account.svg'
import myhome from '../images/myhome.svg'
import loop from '../images/loop-studios.svg'
import faceit from '../images/faceit.svg'
import shortly from '../images/shortly.svg'
import insure from '../images/insure.svg'
import eyecam from '../images/eyecam-co.svg'
import air from '../images/the-air-filter-company.svg'
import close from '../images/icon-remove.svg'
import Container from './container';
import data from '../data';



function App() {
  //const [mode, setMode] = useState(false)
  // const mobile = window.matchMedia('(max-width: 475px)');
  //  let modeMobile;
  //  if (mobile.matches) {
  //   modeMobile = `url(${headerMobile})`;
  //  }else{
  //   modeMobile = `url(${headerDesktop})`;
  //  }
  const [showBox, setShowBox] = useState(false)
  const [selectedItems , setSelectedItems] = useState([])
  const [filter, setFilter] = useState(data)



  function show (e){
    const text = e.target.textContent.trim()

    const filters = [...selectedItems, text];
    const filteredData = data.filter((item) =>{
      return filters.every(filter => [item.role, item.level, ...item.languages, ...item.tools].includes(filter))

    });


    !selectedItems.includes(text) && setSelectedItems(prev => [...prev ,text])
    setShowBox(true)
    setFilter(filteredData)
  }
  function hide(){
    setShowBox(false)
  }
  function del(index) {
    const updatedItems = [...selectedItems];
    updatedItems.splice(index, 1);
    setSelectedItems(updatedItems);

    const filteredData = data.filter((item) =>{
      return updatedItems.every(filter => [item.role, item.level, ...item.languages, ...item.tools].includes(filter))

    });
    setFilter(filteredData)
  }

  const toggleFilter = (filter) => {
    if (selectedItems.includes(filter)) {
        // Filter already exists, remove it
        const updatedFilters = selectedItems.filter((item) => item !== filter);
        setSelectedItems(updatedFilters);
    } else {
        // Add new filter
        setSelectedItems([...selectedItems, filter]);
    }
};

// Clear all filters
const clearFilters = () => {
    setSelectedItems([]);
};

  useEffect(() => {
    // Filter data based on selected items
    if (selectedItems.length > 0) {
        const filtered = data.filter((item) =>
            selectedItems.every((filter) =>
                [item.role, item.level, ...item.languages, ...item.tools].includes(filter)
            )
        );
        setFilteredData(filtered);
    } else {
        // No filters selected, show all data
        setFilteredData(data);
    }
}, [selectedItems]);
  return (
    <div className="App">
      <div className='bg'><img src={window.innerWidth > 1024 ? headerDesktop : headerMobile}/></div>
      <main>
        <div className={showBox ? 'show' : 'hide'}>{selectedItems && selectedItems.map((item, index) =><div key={index}>
        <span className='details'>{item}</span>
          <span className='close' onClick={  () => del(index)}><img src={close} /></span> </div>)}
          <div><p onClick={hide}>Clear</p></div></div> 


      {filter.map((item, index) => {
        return (

          <section>
          <Container
          key= {index}
      image = {photosnap}
      company = {data[0].company}
      new = 'NEW!'
      featured = "FEATURED"
      role = {data[0].role}
      level = {data[0].level}
      position = {data[0].position}
      postedAt = {data[0].postedAt}
      contract = {data[0].contract}
      location = {data[0].location}
      languages = {data[0].languages}
      show = {show}
       box = "box border"
      />
      <Container
       key= {index}
      image = {manage}
      company = {data[1].company}
      new = 'NEW!'
      featured = ""
      role = {data[1].role}
      level = {data[1].level}
      position = {data[1].position}
      postedAt = {data[1].postedAt}
      contract = {data[1].contract}
      location = {data[1].location}
      languages = {data[1].languages}
      tools = {data[1].tools}
      show = {show}
      box = "box border"
      />
   <Container
    key= {index}
      image = {account}
      company = {data[2].company}
      new = ''
      featured = ""
      role = {data[2].role}
      level = {data[2].level}
      position = {data[2].position}
      postedAt = {data[2].postedAt}
      contract = {data[2].contract}
      location = {data[2].location}
      languages = {data[2].languages}
      show = {show}
      box = "box"
      />
       <Container
        key= {index}
      image = {myhome}
      company = {data[3].company}
      new = ''
      featured = ""
      role = {data[3].role}
      level = {data[3].level}
      position = {data[3].position}
      postedAt = {data[3].postedAt}
      contract = {data[3].contract}
      location = {data[3].location}
      languages = {data[3].languages}
      show = {show}
      box = "box"
      />
       <Container
        key= {index}
      image = {loop}
      company = {data[4].company}
      new = ''
      featured = ""
      role = {data[4].role}
      level = {data[4].level}
      position = {data[4].position}
      postedAt = {data[4].postedAt}
      contract = {data[4].contract}
      location = {data[4].location}
      languages = {data[4].languages}
      show = {show}
      box = "box"
      />
       <Container

      image = {faceit}
      company = {data[5].company}
      new = ''
      featured = ""
      role = {data[5].role}
      level = {data[5].level}
      position = {data[5].position}
      postedAt = {data[5].postedAt}
      contract = {data[5].contract}
      location = {data[5].location}
      languages = {data[5].languages}
      show = {show}
      box = "box"
      />
       <Container
      image = {shortly}
      company = {data[6].company}
      new = ''
      featured = ""
      role = {data[6].role}
      level = {data[6].level}
      position = {data[6].position}
      postedAt = {data[6].postedAt}
      contract = {data[6].contract}
      location = {data[6].location}
      languages = {data[6].languages}
      show = {show}
      box = "box"
      />
       <Container
      image = {insure}
      company = {data[7].company}
      new = ''
      featured = ""
      role = {data[7].role}
      level = {data[7].level}
      position = {data[7].position}
      postedAt = {data[7].postedAt}
      contract = {data[7].contract}
      location = {data[7].location}
      languages = {data[7].languages}
      show = {show}
      box = "box"
      />
       <Container
      image = {eyecam}
      company = {data[8].company}
      new = ''
      featured = ""
      role = {data[8].role}
      level = {data[8].level}
      position = {data[8].position}
      postedAt = {data[8].postedAt}
      contract = {data[8].contract}
      location = {data[8].location}
      languages = {data[8].languages}
      show = {show}
      box = "box"
      />
       <Container
      image = {air}
      company = {data[9].company}
      new = ''
      featured = ''
      role = {data[9].role}
      level = {data[9].level}
      position = {data[9].position}
      postedAt = {data[9].postedAt}
      contract = {data[9].contract}
      location = {data[9].location}
      languages = {data[9].languages}
      show = {show}
      box = "box"
      /> 
       </section>
        )
      })}


      </main>
    </div>
  );
}

export default App;
