import { useState } from 'react';
import './App.scss';
import headerDesktop from '../images/bg-header-desktop.svg'
import headerMobile from '../images/bg-header-mobile.svg'
import photosnap from '../images/photosnap.svg'
import manage from '../images/manage.svg'
import account from '../images/account.svg'
import myhome from '../images/myhome.svg'
import loop from '../images/loop-studios.svg'
import faceit from '../images/faceit.svg'
import shortly from '../images/shortly.svg'
import insure from '../images/insure.svg'
import eyecam from '../images/eyecam-co.svg'
import air from '../images/the-air-filter-company.svg'
import Container from './container';
import data from '../data';


function App() {
  //const [mode, setMode] = useState(false)
  // const mobile = window.matchMedia('(max-width: 475px)');
  //  let modeMobile;
  //  if (mobile.matches) {
  //   modeMobile = `url(${headerMobile})`;
  //  }else{
  //   modeMobile = `url(${headerDesktop})`;
  //  }
  return (
    <div className="App">
      <div className='bg'><img src={headerDesktop}/></div>
      <main>
      <Container
      image = {photosnap}
      company = {data[0].company}
      new = 'NEW!'
      featured = 'FEATURED'
      role = {data[0].role}
      level = {data[0].level}
      position = {data[0].position}
      postedAt = {data[0].postedAt}
      contract = {data[0].contract}
      location = {data[0].location}
      languages = {data[0].languages}
      />
       <Container
      image = {manage}
      company = {data[1].company}
      new = 'NEW!'
      featured = 'FEATURED'
      role = {data[1].role}
      level = {data[1].level}
      position = {data[1].position}
      postedAt = {data[1].postedAt}
      contract = {data[1].contract}
      location = {data[1].location}
      languages = {data[1].languages.slice(0)}
      tools = {data[1].tools}
      />
      </main>
    </div>
  );
}

export default App;
