import React, { useState } from 'react';
import './App.scss';
import headerDesktop from '../images/bg-header-desktop.svg';
import headerMobile from '../images/bg-header-mobile.svg';
import close from '../images/icon-remove.svg';
import Container from './Container';
import data from '../data';

function App() {
  const [selectedItems, setSelectedItems] = useState([]);
  const [filteredData, setFilteredData] = useState(data);

  // Function to handle filter selection
  function show(e) {
    const text = e.target.textContent.trim();
    if (!selectedItems.includes(text)) {
      setSelectedItems((prev) => [...prev, text]);
    }

    // Filter data based on selected items
    const filters = [...selectedItems, text];
    const newData = data.filter((item) =>
      filters.every((filter) =>
        [item.role, item.level, ...item.languages, ...item.tools].includes(filter)
      )
    );
    setFilteredData(newData);
  }

  // Function to clear all selected filters
  function clearFilters() {
    setSelectedItems([]);
    setFilteredData(data); // Reset to original data
  }

  // Function to remove a selected filter item
  function removeFilter(index) {
    const updatedItems = [...selectedItems];
    updatedItems.splice(index, 1);
    setSelectedItems(updatedItems);

    // Re-filter data based on remaining selected items
    const newData = data.filter((item) =>
      updatedItems.every((filter) =>
        [item.role, item.level, ...item.languages, ...item.tools].includes(filter)
      )
    );
    setFilteredData(newData);
  }

  return (
    <div className="App">
      <div className="bg">
        <img src={window.innerWidth > 1024 ? headerDesktop : headerMobile} alt="Header" />
      </div>
      <main>
        {/* Display selected filters */}
        <div className={selectedItems.length > 0 ? 'show' : 'hide'}>
          {selectedItems.map((item, index) => (
            <div key={index}>
              <span className="details">{item}</span>
              <span className="close" onClick={() => removeFilter(index)}>
                <img src={close} alt="Close" />
              </span>
            </div>
          ))}
          <div>
            <p onClick={clearFilters}>Clear</p>
          </div>
        </div>

        {/* Display filtered job containers */}
        {filteredData.map((item, index) => (
          <ontainer
            key={index}
            image={item.image}
            company={item.company}
            new={item.new}
            featured={item.featured}
            role={item.role}
            level={item.level}
            position={item.position}
            postedAt={item.postedAt}
            contract={item.contract}
            location={item.location}
            languages={item.languages}
            tools={item.tools}
            show={show}
            box="box border"
          />
        ))}
      </main>
    </div>
  );
}

export default App;
