import React from 'react';

export default function Container(props) {
  function showing(e) {
    props.show(e);
  }

  return (
    <div className='container'>
      <div className={props.box}>
        <div className='flex'>
          <div className='roundImage'>
            {/* Map through props.image to render logo images */}
            {props.image.map((logo, index) => (
              <img key={index} src={logo} alt="Company Logo" />
            ))}
          </div>
          <div className="text">
            <p>
              <span className='name'>{props.company}</span>
              {/* Render "NEW!" label if props.new is true */}
              {props.new && <span className="new">{props.new}!</span>}
              {/* Render "FEATURED" label if props.featured is true */}
              {props.featured && <span className="featured">FEATURED</span>}
            </p>
            <h4>{props.position}</h4>
            <div className="flex">
              <span className="date">{props.postedAt}</span>
              <span className="dot">.</span>
              <span className="date">{props.contract}</span>
              <span className="dot">.</span>
              <span className="date">{props.location}</span>
            </div>
          </div>
        </div>
        <div className='properties'>
          <div onClick={showing}>{props.role}</div>
          <div onClick={showing}>{props.level}</div>
          {/* Map through props.languages to render language elements */}
          {props.languages.map((language, index) => (
            <div key={index} onClick={showing}>{language}</div>
          ))}
          {/* Check if props.tools is defined and render tools */}
          {props.tools && props.tools.length > 0 && (
            <div>{props.tools.join(', ')}</div> // Render tools as a comma-separated string
          )}
        </div>
      </div>
    </div>
  );
}
